import { useState } from "react";

export default function VoiceTest() {
  const [listening, setListening] = useState(false);
  const [transcript, setTranscript] = useState("");
  const [status, setStatus] = useState("Click the mic to start speaking");

  const startListening = () => {
    // Check browser support
    const SpeechRecognition =
      window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SpeechRecognition) {
      setStatus("Speech recognition is not supported in this browser");
      return;
    }

    const recognition = new SpeechRecognition();
    recognition.lang = "en-US";
    recognition.interimResults = false;
    recognition.maxAlternatives = 1;

    // optional: short beep
    const ctx = new AudioContext();
    const osc = ctx.createOscillator();
    osc.frequency.setValueAtTime(600, ctx.currentTime);
    osc.connect(ctx.destination);
    osc.start();
    osc.stop(ctx.currentTime + 0.15);

    setListening(true);
    setStatus("Listening...");
    recognition.start();

    recognition.onresult = (event) => {
      const text = event.results[0][0].transcript.trim();
      setTranscript(text);
      setStatus("Done ✅");
      setListening(false);
    };

    recognition.onerror = (e) => {
      setStatus("Error: " + e.error);
      setListening(false);
    };
  };

  return (
    <div style={{ textAlign: "center", padding: "25px" }}>
      <h1>Voice Test</h1>
      <button
        onClick={startListening}
        disabled={listening}
        style={{
          padding: "12px 24px",
          fontSize: "10px",
          background: listening ? "#aaa" : "#007bff",
          color: "white",
          border: "none",
          borderRadius: "8px",
          cursor: listening ? "not-allowed" : "pointer",
        }}
      >
        {listening ? "Listening..." : "Start Listening"}
      </button>

      <div style={{ marginTop: "20px", fontSize: "22px", fontStyle: "italic" }}>
        {transcript ? `You said: "${transcript}"` : "Say something!"}
      </div>

      <p style={{ color: "gray" }}>{status}</p>
    </div>
  );
}
